package com.sytax.class07;

public class classWhileLoop02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num =1;// value is always =1. it will print infinite times of 1.
while (num<=10) {//
	//num++; before execution the we will got: 2+1=3+1=4+1=5+1=6+1=7+1=8+1=9+1=10+1=11
System.out.print(num+" ");//it will print in line with added space
num++;//1+1=2+1=3+1=4+1=5+1=6+1=7+1=8+1=9+1=10 code ends
	}

}
}